import React , {useState} from "react";

var EARTH = () => {
    const [longitude,setLongitude] = useState(null);
    const [latitude,setLatitude] = useState(null);
    const [dim,setDim] = useState(null);
    const [image,setImage] = useState(null);
    function latInp(event){
        setLongitude(event.target.value)
    }
    function longInp(event){
        setLatitude(event.target.value)
    }
    function dimInp(event){
        setDim(event.target.value)
    }
    function getImage(){
        setImage(`https://api.nasa.gov/planetary/earth/imagery?lon=-95.33&lat=29.78&date=2018-01-01&dim=0.15&api_key=DEMO_KEY`)
    }
  return (
    <>
      <div className="abt">
        <h1>Welcome To Earth</h1>
        <p>
          Welcome to Earth , A Service where you can get the image of any
          perticular latitude and longitude with the date specified in the Welp
          only one way to find out! ðŸ‘‡
        </p>
      </div>
      <div class="form-control">
        <div className="flex space-x-3 center">
          <div class="flex inp space-x-2">
            <input
              placeholder="Latitute in float (eg: -95.33)"
              class="w-full input input-primary input-bordered"
              type="text"
              onChange={latInp}
            />
          </div>
          <div class="flex inp space-x-2">
            <input
              placeholder="Longitude in float (eg: 29.78)"
              class="w-full input input-primary input-bordered"
              type="text"
              onChange={longInp}
            />
          </div>
          <div class="flex inp space-x-2">
            <input
              placeholder="Search"
              class="w-full input input-primary input-bordered"
              type="text"
              onChange={dimInp}
            />
            <button class="btn btn-primary">go</button>
          </div>
        </div>
      </div>
      <div className="img-main">
          <img src="https://api.nasa.gov/planetary/earth/imagery?lon=-95.33&lat=29.78&date=2018-01-01&dim=0.15&api_key=DEMO_KEY" alt="the image is not loaded" />
      </div>
    </>
  );
};
export default EARTH;
