import React from "react";


const Cards = (props) => {
  return(
    <div
      class="flex items-center w-full px-4 py-10 bg-cover card bg-base-200"
      style={props.bgimg}
    >
      <div class="card glass lg:card-side text-neutral-content">
        <figure class="p-6">
          <img src={props.img} class="rounded-lg shadow-lg" />
        </figure>
        <div class="max-w-md card-body">
          <h2 class="card-title">{props.heading}</h2>
          <p>{props.para}</p>
          <div class="card-actions">
            <button class="btn glass rounded-full">Get Started</button>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Cards;
