import React, { useState, useEffect } from "react";

var ByDate = () => {
  const [date, setDate] = useState(null);
  const [info, setInfo] = useState(null);
  async function getInfo() {
    const res = await fetch(
      `https://api.nasa.gov/planetary/apod?api_key=m5qSFJLeqz6oshoKMC0S8QChxUfWerz4VdqOiFo8&date=${date}`
    );
    const dataNew = await res.json();
    await setInfo(dataNew);
  }
  function storeDate(event) {
    event.preventDefault();
    setDate(event.target.value);
    console.log(date);
  }

  return (
    <>
      <div class="form-control">
        <label class="label">
          <span class="label-text">Search by date</span>
        </label>
        <div class="relative">
          <input class="input"
            onChange={storeDate}
            type="text"
            placeholder="YYYY-MM-DD"
            class="w-full pr-16 input input-primary input-bordered"
          />
          //ok done now where is result?
          <button
            onClick={getInfo}
            class="absolute top-0 right-0 rounded-l-none btn btn-primary"
          >
            go
          </button>
        </div>
      </div>
      {info != null ? (
        <div className="card lg:card-side blick main-card card-bordered result">
          <figure>
            <img src={info.hdurl} className="apod" />
          </figure> 
          <div className="card-body">
            <h2 className="card-title">{info.title}</h2>
            <p>{info.explanation}</p>
            <div className="card-actions">
              <button className="btn btn-primary">Get Started</button>
              <button className="btn btn-ghost">More info</button>
            </div>
          </div>
        </div>
      ) : (
        <h1 class="h1-bydate">
          Write the date and press go to get the apod of that day
        </h1>
      )}
    </>
  );
};
export default ByDate;
